package main.java.command;

public interface CommandAdicionarPalavraNaLista 
{
	public boolean adicionarPalavraNaLista();
	public void desfazerAdicionarPalavraNaLista();
	public String getPalavraAdicionar();

}

package main.java.command;

public interface CommandRemoverPalavrasDaLista 
{
	public void removerPalavrasDaLista();
	public void desfazerRemocaoPalavrasDaLista();

}

package main.java.command;

import main.java.fachadaparadao.FachadaDAO;

public class ConcreteCommandAdicionarPalavraNaLista implements CommandAdicionarPalavraNaLista
{
	private String palavraAdicionar;
	private String traducaoPalavraAdicionar;
	private String nomeDaListaAdicionarPalavra;


	public ConcreteCommandAdicionarPalavraNaLista(String palavraAdd, String traducaoAdd, String nomeLista)
	{
		this.palavraAdicionar = palavraAdd;
		this.traducaoPalavraAdicionar = traducaoAdd;
		this.nomeDaListaAdicionarPalavra = nomeLista;
	}


	public String getPalavraAdicionar()
	{
		return this.palavraAdicionar;
	}


	public boolean adicionarPalavraNaLista()
	{
		boolean conseguiuAdicionarPalavraNaLista;
		conseguiuAdicionarPalavraNaLista = 
				FachadaDAO.getFachadaDAO().adicionarNovaPalavraNaLista(nomeDaListaAdicionarPalavra, palavraAdicionar, traducaoPalavraAdicionar);

		return conseguiuAdicionarPalavraNaLista;
	}


	public void desfazerAdicionarPalavraNaLista()
	{
		FachadaDAO.getFachadaDAO().removerPalavraDaLista(nomeDaListaAdicionarPalavra, traducaoPalavraAdicionar);
	}



}

package main.java.command;

import java.util.LinkedList;

import main.java.fachadaparadao.FachadaDAO;


public class ConcreteCommandRemoverPalavrasDaLista implements CommandRemoverPalavrasDaLista
{
	private LinkedList<String> palavrasRemover;
	private LinkedList<String> traducoesDasPalavrasRemover;
	private String nomeDaListaRemoverPalavra;

	public ConcreteCommandRemoverPalavrasDaLista(LinkedList<String> palavrasRemover, LinkedList<String> traducoesDasPalavrasRemover, String nomeDaListaRemoverPalavras)
	{
		this.palavrasRemover = palavrasRemover;
		this.traducoesDasPalavrasRemover = traducoesDasPalavrasRemover; 
		this.nomeDaListaRemoverPalavra = nomeDaListaRemoverPalavras;
	}

	public void removerPalavrasDaLista()
	{
		for(int i = 0; i < palavrasRemover.size(); i++)
		{
			String traducaoPalavraRemover = this.traducoesDasPalavrasRemover.get(i);
			FachadaDAO.getFachadaDAO().removerPalavraDaLista(this.nomeDaListaRemoverPalavra, traducaoPalavraRemover);
		}
	}

	public void desfazerRemocaoPalavrasDaLista()
	{
		for(int i = 0; i < palavrasRemover.size(); i++)
		{
			String palavraInserirNovamente = this.palavrasRemover.get(i);
			String traducaoPalavraInserirNovamente = this.traducoesDasPalavrasRemover.get(i);

			FachadaDAO.getFachadaDAO().adicionarNovaPalavraNaLista(nomeDaListaRemoverPalavra, palavraInserirNovamente, traducaoPalavraInserirNovamente);
		}
	}

}

package main.java.dao;

import java.io.BufferedReader;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.Reader;

public class ConcreteDAOConfiguracoesDeJogoArquivos  implements DAOConfiguracoesDeJogo
{
	@Override
	public String[] lerConfiguracoesJogo() 
	{
		try
		{
			String [] configuracoes = new String[2];
			FileInputStream leitor = new FileInputStream("textos/configuracoes.txt");
			Reader reader = new InputStreamReader(leitor, "UTF-8");
			BufferedReader filtroLeitura = new BufferedReader(reader);
			String stringLida = filtroLeitura.readLine();
			int linhaLida = 0;
			while(stringLida != null)
			{
				String [] stringLidaSplit = stringLida.split(":");
				configuracoes[linhaLida] = stringLidaSplit[1];
				stringLida = filtroLeitura.readLine();
				linhaLida = linhaLida + 1;
			}

			filtroLeitura.close();
			reader.close();
			leitor.close();
			return configuracoes;

		}
		catch(IOException exc)
		{
			System.out.println("erro na leitura das configuracoes do jogo:" + exc);
			return null;
		}
	}

	@Override
	public void setarNovasConfiguracoesJogo(int tempoParaSetarBomba,
			int tempoParaBombaExplodir) 
	{
		try 
		{	
			FileOutputStream escreve = new FileOutputStream("textos/configuracoes.txt");
			OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");

			filtroEscreve.write("segundos ate inserir novas bombas:" + tempoParaSetarBomba + "\r\n");
			filtroEscreve.write("segundos ate uma bomba explodir:" + tempoParaBombaExplodir + "\r\n");

			filtroEscreve.flush();
			filtroEscreve.close();
			escreve.close();
		} 
		catch (IOException e) 
		{
			System.out.println(e);
		}

	}

}

package main.java.dao;

import java.io.BufferedReader;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.Reader;


public class ConcreteDAOListaDePalavrasArquivos implements DAOListaDePalavras
{
	@Override
	public boolean criarNovaListaPalavras(String nomeLista) 
	{
		try
		{
			//primeiro, vamos ver se a lista já existe
			boolean listaJahExiste = this.listaJahExiste(nomeLista);

			if(listaJahExiste == false)
			{
				String enderecoLista = "textos/" + nomeLista + ".txt";
				FileOutputStream escreve = new FileOutputStream(enderecoLista);
				OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");
				filtroEscreve.flush();
				filtroEscreve.close();
				escreve.close();
				return true;
			}
			else
			{
				return false;
			}

		}
		catch(IOException e)
		{
			return false;
		}	

	}

	private boolean listaJahExiste(String nomeLista)//ve se uma lista com esse nome jah existe no jogo.
	{
		boolean listaExiste = false;
		String arquivoLista = nomeLista + ".txt";
		File pastaListas = new File("textos");
		String[] nomesListasPasta = pastaListas.list();
		String percorredorNomesListasPasta = "";
		for(int i = 0; i < nomesListasPasta.length; i++)
		{
			percorredorNomesListasPasta = nomesListasPasta[i];
			if(percorredorNomesListasPasta.compareTo(arquivoLista) == 0)
			{
				listaExiste = true;
				break;
			}
		}
		return listaExiste;
	}


	@Override
	public boolean removerListaPalavras(String nomeLista) 
	{
		//primeiro, vamos ver se a lista existe
		boolean listaJahExiste = this.listaJahExiste(nomeLista);

		if(listaJahExiste == false)
		{
			return false;
		}
		else
		{
			if(nomeLista.compareTo("palavras") == 0)
			{
				return false;
			}
			else
			{
				String nomeArquivoLista = "textos/" + nomeLista + ".txt";
				File arquivoLista = new File(nomeArquivoLista);
				arquivoLista.delete();
				return true;
			}
		}	
	}

	@Override
	public boolean limparListaPalavras(String nomeLista) 
	{
		//primeiro, vamos ver se a lista existe
		boolean listaJahExiste = this.listaDePalavrasJahExiste(nomeLista);

		if(listaJahExiste == false)
		{
			return false;
		}
		else
		{
			try
			{
				String nomeArquivoLista = "textos/" + nomeLista + ".txt";
				FileOutputStream escreve = new FileOutputStream(nomeArquivoLista);
				OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");
				filtroEscreve.close();
				escreve.close();
				return true;
			}
			catch(IOException exc)
			{
				return false;
			}	
		}

	}

	@Override
	public String[] extrairNomesDeTodasAsListasDePalavras() 
	{
		File file = new File("textos");
		String[] nomesDasListas = file.list();
		String[] nomesListasSohPalavras = new String[nomesDasListas.length - 3];
		/* na pasta "textos",
			alem das listas de palavras que podem 
			ser usadas no jogo, há o placar e as configuracões. vamos remover os dois.*/
		int percorreNomesListasSohPalavras = 0;
		for(int i = 0; i < nomesDasListas.length; i++)
		{
			if(nomesDasListas[i].compareTo("configuracoes.txt") != 0 &&
					nomesDasListas[i].compareTo("highscore.txt") != 0 && nomesDasListas[i].compareTo("listaaserusadanojogo.txt") != 0)
			{
				nomesListasSohPalavras[percorreNomesListasSohPalavras] = nomesDasListas[i];
				percorreNomesListasSohPalavras = percorreNomesListasSohPalavras + 1;
			}
		}


		return nomesListasSohPalavras;
	}

	@Override
	public boolean listaDePalavrasJahExiste(String nomeLista)//v� se uma lista com esse nome já existe no jogo. 
	{
		boolean listaExiste = false;
		String arquivoLista = nomeLista + ".txt";
		File pastaListas = new File("textos");
		String[] nomesListasPasta = pastaListas.list();
		String percorredorNomesListasPasta = "";
		for(int i = 0; i < nomesListasPasta.length; i++)
		{
			percorredorNomesListasPasta = nomesListasPasta[i];
			if(percorredorNomesListasPasta.compareTo(arquivoLista) == 0)
			{
				listaExiste = true;
				break;
			}
		}
		return listaExiste;
	}

	@Override
	public boolean mudarListaASerUsadaNoJogo(String nomeListaAtual) 
	{
		try
		{
			String nomeArquivoListaJogo = "textos/listaaserusadanojogo.txt";
			FileOutputStream escreve = new FileOutputStream(nomeArquivoListaJogo);
			OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");
			filtroEscreve.write(nomeListaAtual);
			filtroEscreve.close();
			escreve.close();
			return true;
		}
		catch(IOException exc)
		{
			System.out.println("erro I/O na funcionalidade jogar com lista visualizada:" + exc);
			return false;
		}

	}

	@Override
	public String pegarNomeListaASerUsadaNoJogo() 
	{
		try
		{
			FileInputStream leitor = new FileInputStream("textos/listaaserusadanojogo.txt");
			Reader reader = new InputStreamReader(leitor, "UTF-8");
			BufferedReader buff = new BufferedReader(reader);
			String listaASerUsadaNoJogo = buff.readLine();
			return listaASerUsadaNoJogo;
		}
		catch(FileNotFoundException e) 
		{
			System.out.println("lista a ser usada no jogo nao encontrada");
			return "palavras";
		}
		catch(IOException exc)
		{
			System.out.println("excecao IO ao tentar pegar lista a ser usada no jogo");
			return "palavras";
		}
	}



}

package main.java.dao;

import java.io.BufferedReader;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.Reader;
import java.util.LinkedList;

import main.java.jogo.PalavrasETraducoes;



public class ConcreteDAOPalavrasDeListaArquivos implements DAOPalavrasDeLista
{
	@Override
	public PalavrasETraducoes extrairPalavrasETraducoes(String listaOndeEstaoOsDados) 
	{
		try 
		{
			String localArquivo = "textos/" + listaOndeEstaoOsDados + ".txt";
			FileInputStream leitor = new FileInputStream(localArquivo);
			Reader reader = new InputStreamReader(leitor, "UTF-8");
			BufferedReader buff = new BufferedReader(reader);
			String linhaDoArquivo = buff.readLine();

			int percorredorPalavras = 0;
			int percorredorTraducoes = 0;

			LinkedList<String> palavras = new LinkedList<String>();
			LinkedList<String> traducoes = new LinkedList<String>();

			while(linhaDoArquivo != null)
			{
				String[] linhaSplitada = linhaDoArquivo.split(";");
				palavras.add(percorredorPalavras, linhaSplitada[0]);
				percorredorPalavras = percorredorPalavras + 1;
				traducoes.add(percorredorTraducoes, linhaSplitada[1]);
				percorredorTraducoes = percorredorTraducoes + 1;
				linhaDoArquivo = buff.readLine();
			}

			buff.close();
			reader.close();
			leitor.close();
			PalavrasETraducoes palavrasETraducoesDesordenadas = new PalavrasETraducoes(palavras, traducoes);
			PalavrasETraducoes palavrasETraducoesOrdenadas = this.ordenarPalavrasETraducoes(palavrasETraducoesDesordenadas);

			return palavrasETraducoesOrdenadas;
		} 
		catch(FileNotFoundException e) 
		{
			System.out.println("lista nao encontrada:" + listaOndeEstaoOsDados);
			return null;
		}
		catch(IOException exc)
		{
			System.out.println("excecao IO");
			return null;
		}
	}

	private PalavrasETraducoes ordenarPalavrasETraducoes(PalavrasETraducoes listaDePalavrasETraducoes)
	{
		PalavrasETraducoes listaOrdenada;
		LinkedList<String> palavras = listaDePalavrasETraducoes.getPalavras();
		LinkedList<String> traducoes = listaDePalavrasETraducoes.getTraducoes();

		if(palavras.size() > 1)
		{
			int percorredorPalavras = 0;
			String auxiliarTroca;//auxiliar para trocas na ordenacao das palavras.
			while(percorredorPalavras < palavras.size())
			{
				for(int i = percorredorPalavras + 1; i < palavras.size(); i++)
				{
					//if abaixo compara duas palavras da lista de palavras pra ver se a da esquerda 
					//vem antes da palavra na direita
					if(palavras.get(i).compareTo(palavras.get(percorredorPalavras)) < 0)
					{
						auxiliarTroca = palavras.get(i);
						palavras.set(i, palavras.get(percorredorPalavras));
						palavras.set(percorredorPalavras, auxiliarTroca);

						auxiliarTroca = traducoes.get(i);
						traducoes.set(i, traducoes.get(percorredorPalavras));
						traducoes.set(percorredorPalavras, auxiliarTroca);
					}
				}
				percorredorPalavras = percorredorPalavras + 1;
			}
		}

		listaDePalavrasETraducoes.setPalavras(palavras);
		listaDePalavrasETraducoes.setTraducoes(traducoes);
		listaOrdenada = listaDePalavrasETraducoes;

		return listaOrdenada;
	}

	@Override
	public boolean adicionarNovaPalavraNaLista(String nomeLista,
			String palavraNova, String traducaoNova) 
	{

		PalavrasETraducoes listaPalavrasETraducoes = this.extrairPalavrasETraducoes(nomeLista);
		if(listaPalavrasETraducoes != null)
		{
			int quantasPalavrasNoArquivo = listaPalavrasETraducoes.getNumPalavrasNoJogo();
			LinkedList<String> palavras = new LinkedList<String>();
			LinkedList<String> traducoes = new LinkedList<String>();

			for(int i = 0; i < quantasPalavrasNoArquivo; i++)
			{
				palavras.add(listaPalavrasETraducoes.getPalavra(i));
				traducoes.add(listaPalavrasETraducoes.getTraducao(i));
			}

			String novaPalavra = palavraNova;
			String novaTraducao = traducaoNova;

			palavras.add(novaPalavra);
			traducoes.add(novaTraducao);

			//agora vamos adicionar nova palavra no txt

			try
			{
				String enderecoLista = "textos/" + nomeLista + ".txt";
				FileOutputStream escreve = new FileOutputStream(enderecoLista);
				OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");
				for(int j = 0; j < palavras.size(); j++)
				{
					String linhaNova = palavras.get(j) + ";" + traducoes.get(j);
					filtroEscreve.write(linhaNova +"\r\n");
				}

				filtroEscreve.flush();
				filtroEscreve.close();
				escreve.close();
				return true;
			}
			catch(IOException e)
			{
				System.out.println("excecao IO ao tentar escrever no arquivo palavras.txt");
			}
		}

		return false;
	}

	@Override
	public void removerPalavraDaLista(String nomeDalista,
			String traducaoDaPalavraASerRemovida) 
	{
		PalavrasETraducoes listaDePalavrasETraducoes = this.extrairPalavrasETraducoes(nomeDalista);
		int quantasPalavrasNoArquivo = listaDePalavrasETraducoes.getNumPalavrasNoJogo();
		LinkedList<String> palavras = new LinkedList<String>();
		LinkedList<String> traducoes = new LinkedList<String>();

		for(int i = 0; i < quantasPalavrasNoArquivo; i++)
		{
			palavras.add(listaDePalavrasETraducoes.getPalavra(i));
			traducoes.add(listaDePalavrasETraducoes.getTraducao(i));
		}

		String traducaoPalavraRemovida = traducaoDaPalavraASerRemovida;
		if(traducaoPalavraRemovida.compareTo("") == 0)
		{
			System.out.println("traducao da palavra a ser removida nao informada");
		}
		else
		{
			//acharemos a palavra a ser removida
			int indiceParaRemocao = -1;

			for(int i = 0; i < traducoes.size(); i++)
			{
				String traducao = traducoes.get(i);
				if(traducao.compareTo(traducaoPalavraRemovida) == 0)
				{
					indiceParaRemocao = i;
				}
			}

			if(indiceParaRemocao == -1)
			{
				System.out.println("traducao nao encontrada");
			}
			else
			{
				traducoes.remove(indiceParaRemocao);
				palavras.remove(indiceParaRemocao);

				//agora vamos inserir o resto das palavras no txt

				try
				{
					String enderecoLista = "textos/" + nomeDalista + ".txt";
					FileOutputStream escreve = new FileOutputStream(enderecoLista);
					OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8"); 
					for(int j = 0; j < palavras.size(); j++)
					{
						String linhaNova = palavras.get(j) + ";" + traducoes.get(j);
						filtroEscreve.write(linhaNova +"\r\n");
					}

					filtroEscreve.flush();
					filtroEscreve.close();
					escreve.close();

				}
				catch(IOException e)
				{
					System.out.println("excecao IO ao tentar escrever no arquivo " + nomeDalista + ".txt");
				}
			}
		}
	}

}

package main.java.dao;

import java.io.BufferedReader;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.io.Reader;

public class ConcreteDAORankingArquivos implements DAORanking
{
	@Override
	public void inserirJogadorRanking(String nomeJogador, int pontuacao) 
	{
		try
		{
			String [] configuracoes = this.lerConfiguracoesJogo();
			String [] ranking = lerRanking();
			if(ranking != null)
			{
				for(int i = 0; i < ranking.length; i++)
				{
					String[] separaRanking = ranking[i].split(";");
					int placarRanking = Integer.valueOf(separaRanking[1]);
					if(placarRanking < pontuacao)
					{
						ranking[i] = nomeJogador + ";" + pontuacao + ";" + configuracoes[0] + ";" + configuracoes[1];
						break;
					}		
				}

				//agora, vamos reescrever no ranking
				FileOutputStream escreve = new FileOutputStream("textos/highscore.txt");
				OutputStreamWriter filtroEscreve = new OutputStreamWriter(escreve, "UTF8");
				for(int j = 0; j < ranking.length; j++)
				{
					filtroEscreve.write(ranking[j] +"\r\n");
				}
				filtroEscreve.flush();
				filtroEscreve.close();
				escreve.close();	

			}
		}
		catch(IOException exc)
		{
			System.err.println("erro na escrita do placar:" + exc);
		}

	}

	public String[] lerConfiguracoesJogo() 
	{
		try
		{
			String [] configuracoes = new String[2];
			FileInputStream leitor = new FileInputStream("textos/configuracoes.txt");
			Reader reader = new InputStreamReader(leitor, "UTF-8");
			BufferedReader filtroLeitura = new BufferedReader(reader);
			String stringLida = filtroLeitura.readLine();
			int linhaLida = 0;
			while(stringLida != null)
			{
				String [] stringLidaSplit = stringLida.split(":");
				configuracoes[linhaLida] = stringLidaSplit[1];
				stringLida = filtroLeitura.readLine();
				linhaLida = linhaLida + 1;
			}

			filtroLeitura.close();
			reader.close();
			leitor.close();
			return configuracoes;

		}
		catch(IOException exc)
		{
			System.out.println("erro na leitura das configuracoes do jogo:" + exc);
			return null;
		}
	}

	@Override
	public String[] lerRanking() 
	{
		try
		{
			String [] ranking = new String [8];

			FileInputStream leitor = new FileInputStream("textos/highscore.txt");
			Reader reader = new InputStreamReader(leitor, "UTF-8");
			BufferedReader filtroLeitura = new BufferedReader(reader);
			String stringLida = filtroLeitura.readLine();
			int linhaLida = 0;
			while(stringLida != null)
			{
				ranking[linhaLida] = stringLida;
				stringLida = filtroLeitura.readLine();
				linhaLida = linhaLida + 1;
			}

			filtroLeitura.close();
			reader.close();
			leitor.close();
			return ranking;
		}
		catch(IOException exc)
		{
			System.err.println("erro na leitura do placar:" + exc);
